    },
    List: async(req,res) =>{
        try {
            var search = req.body.search;
            const Users = await models.User.find({
                $or:[
                    {"name": new RegExp(search, "i")},
                    {"surname": new RegExp(search, "i")},
                    {"email": new RegExp(search, "i")},
                ]
            }).sort({'createdAt': -1});

            res.status (200).json({
                Users: Users,
            });
        } catch (error) {
            res.status (500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); 
        }
    },
    remove: async(req, res) =>{
        try {
            const User = awaitmodels.User.findByIdAndDelete({_id: req.body._id});
            res.status(200).json({
                message: "EL USUARIO SE ELIMINO CORRECTAMENTE",
            });
        } catch (error) {
            res.status (500).send({
                message: "OCURRIO UN PROBLEMA"
            });
            console.log(error); 
        }
    },
}

